package com.syntax.class01;

public class PrintLnVsPrint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// println- prints and creates new line
		// print - prints, does not create new lines, prints in the  same line
		
		System.out.println("Today is Sunday.");
		
		System.out.println("Tomorrow is Monday.");
		
		System.out.println("Then it will be Tuesday.");
		
		System.out.print("Then it's Wednesday.");
		
		System.out.print(" And then it will be Thursday!");

	}

}

